import React from "react";
import { useNavigate } from "react-router-dom";
import {
  Plus,
  Calendar as CalendarIcon,
  FileUp,
  MessageSquare,
  ClipboardCheck,
  Users,
} from "lucide-react";

const DashboardPage: React.FC = () => {
  const navigate = useNavigate();

  const statCards = [
    { icon: ClipboardCheck, title: "Tasks Due", value: 3, color: "bg-blue-100 text-blue-600" },
    { icon: CalendarIcon, title: "Meetings Today", value: 2, color: "bg-green-100 text-green-600" },
    { icon: MessageSquare, title: "Unread Messages", value: 5, color: "bg-purple-100 text-purple-600" },
    { icon: Users, title: "Active Team Members", value: 12, color: "bg-orange-100 text-orange-600" },
  ];

  const recentActivities = [
    { name: "Task 123", desc: 'was moved to "In Progress".' },
    { name: "Jane", desc: "Please review the latest proposal draft." },
  ];

  return (
    <div className="min-h-screen bg-white text-gray-800 px-6 py-8 space-y-8 transition-all duration-300">
      {/* Header Section */}
      <header className="flex flex-col md:flex-row md:items-center md:justify-between">
        <div>
          <h2 className="text-3xl font-bold tracking-tight">Dashboard Overview</h2>
          <p className="text-gray-500 mt-1">Welcome back ðŸ‘‹ Hereâ€™s whatâ€™s happening today.</p>
        </div>
      </header>

      {/* Stats Summary */}
      <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {statCards.map((card, idx) => (
          <div
            key={idx}
            onClick={() => {
              if (card.title.includes("Tasks")) navigate("/tasks");
              if (card.title.includes("Meetings")) navigate("/meetings");
              if (card.title.includes("Messages")) navigate("/messages");
              if (card.title.includes("Team")) navigate("/about"); // or /users if it exists
            }}
            className="flex items-center gap-4 p-5 bg-white rounded-xl shadow-sm hover:shadow-md border border-gray-100 cursor-pointer transition-all hover:scale-[1.02]"
          >
            <div className={`p-3 rounded-lg ${card.color}`}>
              <card.icon size={22} />
            </div>
            <div>
              <p className="text-sm text-gray-500 font-medium">{card.title}</p>
              <h3 className="text-2xl font-bold text-gray-800">{card.value}</h3>
            </div>
          </div>
        ))}
      </section>

      {/* AI Summary + Quick Actions */}
      <section className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* AI Summary */}
        <div className="col-span-2 bg-white border border-gray-100 rounded-2xl shadow-sm p-6">
          <h3 className="text-lg font-semibold border-l-4 border-blue-500 pl-3 mb-3">
            AI Summary
          </h3>
          <p className="text-gray-700">
            Today, you have <strong>3 tasks</strong> due,{" "}
            <strong>2 meetings</strong> scheduled, and{" "}
            <strong>5 unread messages.</strong>
          </p>

          <h4 className="mt-6 font-semibold mb-3 text-gray-800">Recent Activity</h4>
          <div className="space-y-3">
            {recentActivities.map((activity, idx) => (
              <div
                key={idx}
                className="flex items-center gap-3 bg-gray-50 hover:bg-gray-100 p-3 rounded-lg transition"
              >
                <div className="w-8 h-8 rounded-full bg-gray-200 flex items-center justify-center font-semibold text-gray-600">
                  {activity.name.charAt(0)}
                </div>
                <p className="text-gray-700">
                  <strong>{activity.name}</strong> {activity.desc}
                </p>
              </div>
            ))}
          </div>
        </div>

        {/* Quick Actions */}
        <div className="bg-white border border-gray-100 rounded-2xl shadow-sm p-6">
          <h3 className="text-lg font-semibold mb-4">Quick Actions</h3>

          <div className="flex flex-col space-y-3">
            <button
              onClick={() => navigate("/tasks")}
              className="flex items-center justify-center gap-2 bg-blue-600 text-white py-2 px-4 rounded-lg hover:bg-blue-700 transition"
            >
              <Plus size={16} /> <span>New Task</span>
            </button>

            <button
              onClick={() => navigate("/meetings/schedule")}
              className="flex items-center justify-center gap-2 border border-gray-300 py-2 px-4 rounded-lg hover:bg-gray-50 transition"
            >
              <CalendarIcon size={16} /> <span>New Meeting</span>
            </button>

            <button
              onClick={() => navigate("/files/upload")}
              className="flex items-center justify-center gap-2 border border-gray-300 py-2 px-4 rounded-lg hover:bg-gray-50 transition"
            >
              <FileUp size={16} /> <span>Upload File</span>
            </button>
          </div>
        </div>
      </section>
    </div>
  );
};

export default DashboardPage;
