import React from "react";
import {
  LayoutDashboard,
  MessageSquare,
  CalendarDays,
  FileUp,
  ClipboardList,
  LogOut,
  Users,
  Settings,
  Video,
} from "lucide-react";
import { useNavigate, useLocation } from "react-router-dom";
import { AnimatePresence, motion } from "framer-motion";
import { authService } from "../services/authService";

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

const Sidebar: React.FC<SidebarProps> = ({ isOpen, onClose }) => {
  const navigate = useNavigate();
  const location = useLocation();

  const navItems = [
    { label: "Dashboard", icon: LayoutDashboard, path: "/dashboard" },
    { label: "Messages", icon: MessageSquare, path: "/messages" },
    { label: "Tasks", icon: ClipboardList, path: "/tasks" },
    { label: "Meetings", icon: Video, path: "/meetings" },
    { label: "Calendar", icon: CalendarDays, path: "/calendar" },
    { label: "Files", icon: FileUp, path: "/files" },
    { label: "Team", icon: Users, path: "/team" },
    { label: "Settings", icon: Settings, path: "/settings" },
  ];

  return (
    <>
      <AnimatePresence>
        {isOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/5 z-40 lg:hidden"
            onClick={onClose}
          />
        )}
      </AnimatePresence>

      <aside
        className={`fixed top-0 left-0 z-50 h-screen w-64 bg-white border-r border-zinc-100 transform transition-transform duration-300 ease-in-out ${isOpen ? "translate-x-0" : "-translate-x-full"
          } lg:translate-x-0 flex flex-col`}
      >
        {/* Brand Section */}
        <div
          className="p-10 flex items-center gap-3 cursor-pointer"
          onClick={() => navigate("/dashboard")}
        >
          <div className="w-8 h-8 bg-black flex items-center justify-center">
            <img src="/logo1.png" alt="Logo" className="w-5 h-5 object-contain brightness-0 invert" />
          </div>
          <h1 className="text-sm font-bold tracking-[0.2em] uppercase text-black">Collabryta</h1>
        </div>

        {/* Navigation */}
        <nav className="flex-1 space-y-0.5 overflow-y-auto pt-4">
          {navItems.map(({ label, icon: Icon, path }) => {
            const active = location.pathname.startsWith(path);
            return (
              <button
                key={path}
                onClick={() => {
                  navigate(path);
                  onClose();
                }}
                className={`w-full group flex items-center px-10 py-3.5 transition-all duration-200 border-l-2 ${active
                  ? "bg-zinc-50 border-black text-black font-semibold"
                  : "bg-transparent border-transparent text-zinc-400 hover:text-black hover:bg-zinc-50"
                  }`}
              >
                <div className="flex items-center gap-4">
                  <Icon size={16} className={`${active ? "text-black" : "text-zinc-300 group-hover:text-black"} transition-colors`} />
                  <span className="text-[11px] uppercase tracking-widest">{label}</span>
                </div>
              </button>
            );
          })}
        </nav>

        {/* Footer / Logout */}
        <div className="p-10">
          <button
            onClick={() => {
              authService.logout();
              navigate("/");
            }}
            className="flex items-center gap-3 text-[11px] font-bold uppercase tracking-widest text-zinc-400 hover:text-black transition-all"
          >
            <LogOut size={16} />
            Sign Out
          </button>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
