import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import {
    ChevronLeft,
    Calendar,
    User,
    Save,
    Layout,
    Flag,
    CheckCircle2,
    Loader2,
    AlignLeft,
    Clock
} from "lucide-react";
import { taskService } from "../../services/taskService";
import { userService } from "../../services/userService";
import { motion } from "framer-motion";

const AddTaskPage: React.FC = () => {
    const navigate = useNavigate();
    const { id } = useParams<{ id: string }>();
    const isEditing = Boolean(id);

    const [users, setUsers] = useState<any[]>([]);
    const [submitting, setSubmitting] = useState(false);
    const [loading, setLoading] = useState(isEditing);

    const [formData, setFormData] = useState({
        title: "",
        description: "",
        status: "Upcoming",
        priority: "Normal",
        assigned_to: "",
        due_date: new Date().toISOString().split('T')[0]
    });

    useEffect(() => {
        const loadData = async () => {
            try {
                const [usersData, taskData] = await Promise.all([
                    userService.getAllUsers(),
                    isEditing && id ? taskService.getTaskById(Number(id)) : Promise.resolve(null)
                ]);
                setUsers(usersData);
                if (taskData) {
                    setFormData({
                        title: taskData.title,
                        description: taskData.description || "",
                        status: taskData.status === "todo" ? "Upcoming" : taskData.status === "in_progress" ? "In Progress" : taskData.status === "done" ? "Completed" : taskData.status,
                        priority: taskData.priority,
                        assigned_to: taskData.assignee_id ? String(taskData.assignee_id) : "",
                        due_date: taskData.due_date ? taskData.due_date.split("T")[0] : new Date().toISOString().split('T')[0]
                    });
                }
            } catch (e) {
                console.error("Failed to load data", e);
            } finally {
                setLoading(false);
            }
        };
        loadData();
    }, [id, isEditing, navigate]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setSubmitting(true);
        try {
            const payload = {
                ...formData,
                status: formData.status as any,
                priority: formData.priority as any,
                assigned_to: formData.assigned_to ? Number(formData.assigned_to) : undefined
            };

            if (isEditing && id) {
                await taskService.updateTask(Number(id), payload);
            } else {
                await taskService.createTask(payload);
            }
            navigate("/tasks");
        } catch (error) {
            console.error(error);
            setSubmitting(false);
        }
    };

    if (loading) {
        return (
            <div className="min-h-screen bg-slate-100 flex items-center justify-center">
                <Loader2 className="w-8 h-8 text-indigo-600 animate-spin" />
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-slate-100 p-4 md:p-8 flex items-center justify-center">
            <motion.form
                initial={{ opacity: 0, scale: 0.98 }}
                animate={{ opacity: 1, scale: 1 }}
                className="w-full max-w-5xl"
                onSubmit={handleSubmit}
            >
                {/* üß± Grid Layout - Bento Box Style */}
                <div className="grid grid-cols-1 md:grid-cols-12 gap-4 md:gap-6">

                    {/* üß¢ Header / Title Box (Full Width) */}
                    <div className="md:col-span-12 bg-white rounded-2xl p-6 shadow-sm border border-slate-200/60 flex items-center gap-4">
                        <button
                            type="button"
                            onClick={() => navigate(-1)}
                            className="p-2 bg-slate-50 hover:bg-slate-100 text-slate-500 rounded-xl transition-colors"
                        >
                            <ChevronLeft size={24} />
                        </button>
                        <div className="flex-1">
                            <label className="block text-xs font-bold text-slate-400 uppercase tracking-widest mb-1">
                                {isEditing ? 'Editing Task' : 'New Task'}
                            </label>
                            <input
                                type="text"
                                value={formData.title}
                                onChange={e => setFormData({ ...formData, title: e.target.value })}
                                placeholder="Task Title..."
                                required
                                className="w-full text-2xl font-bold text-slate-800 placeholder:text-slate-300 outline-none bg-transparent"
                            />
                        </div>
                        <div className="hidden md:flex items-center gap-2">
                            <div className="w-10 h-10 rounded-full bg-slate-50 flex items-center justify-center text-slate-300">
                                <Layout size={20} />
                            </div>
                        </div>
                    </div>

                    {/* üìù Description Box (Left, Larger) */}
                    <div className="md:col-span-8 bg-white rounded-2xl p-6 shadow-sm border border-slate-200/60 flex flex-col min-h-[400px]">
                        <div className="flex items-center gap-2 mb-4 pb-4 border-b border-slate-50">
                            <AlignLeft size={18} className="text-indigo-500" />
                            <span className="font-bold text-slate-700">Description</span>
                        </div>
                        <textarea
                            value={formData.description}
                            onChange={e => setFormData({ ...formData, description: e.target.value })}
                            placeholder="Add details about this task..."
                            className="flex-1 w-full bg-slate-50/50 rounded-xl p-4 border border-transparent focus:bg-white focus:border-indigo-100 outline-none transition-all resize-none text-slate-600 leading-relaxed text-base"
                        />
                    </div>

                    {/* üéõÔ∏è Sidebar Boxes (Right Column) */}
                    <div className="md:col-span-4 flex flex-col gap-4">

                        {/* 1. Status & Priority Box */}
                        <div className="bg-white rounded-2xl p-5 shadow-sm border border-slate-200/60">
                            <label className="text-xs font-bold text-slate-400 uppercase tracking-wider mb-3 block">State</label>
                            <div className="space-y-3">
                                <div className="flex items-center justify-between p-3 bg-slate-50 rounded-xl">
                                    <div className="flex items-center gap-2 text-slate-600 font-medium text-sm">
                                        <CheckCircle2 size={16} /> Status
                                    </div>
                                    <span className="bg-white px-3 py-1 rounded-lg text-xs font-bold shadow-sm border border-slate-100 text-indigo-600">
                                        {formData.status}
                                    </span>
                                </div>
                                <div className="flex items-center justify-between p-3 bg-slate-50 rounded-xl">
                                    <div className="flex items-center gap-2 text-slate-600 font-medium text-sm">
                                        <Flag size={16} /> Priority
                                    </div>
                                    <span className="bg-white px-3 py-1 rounded-lg text-xs font-bold shadow-sm border border-slate-100 text-slate-600">
                                        {formData.priority}
                                    </span>
                                </div>
                            </div>
                        </div>

                        {/* 2. Assignee Box */}
                        <div className="bg-white rounded-2xl p-5 shadow-sm border border-slate-200/60">
                            <label className="text-xs font-bold text-slate-400 uppercase tracking-wider mb-3 block">Assignment</label>
                            <div className="relative">
                                <select
                                    value={formData.assigned_to}
                                    onChange={e => setFormData({ ...formData, assigned_to: e.target.value })}
                                    className="w-full appearance-none bg-slate-50 border border-slate-200 rounded-xl px-4 py-3 pl-11 text-sm font-medium text-slate-700 outline-none focus:border-indigo-500 cursor-pointer transition-all hover:bg-slate-100"
                                >
                                    <option value="">No Assignee</option>
                                    {users.map(u => (
                                        <option key={u.id} value={u.id}>{u.name}</option>
                                    ))}
                                </select>
                                <User size={18} className="absolute left-3.5 top-1/2 -translate-y-1/2 text-slate-400 pointer-events-none" />
                            </div>
                        </div>

                        {/* 3. Due Date Box */}
                        <div className="bg-white rounded-2xl p-5 shadow-sm border border-slate-200/60">
                            <label className="text-xs font-bold text-slate-400 uppercase tracking-wider mb-3 block">Timeline</label>
                            <div className="relative">
                                <input
                                    type="date"
                                    value={formData.due_date}
                                    onChange={e => setFormData({ ...formData, due_date: e.target.value })}
                                    className="w-full bg-slate-50 border border-slate-200 rounded-xl px-4 py-3 pl-11 text-sm font-medium text-slate-700 outline-none focus:border-indigo-500 cursor-pointer hover:bg-slate-100"
                                />
                                <Clock size={18} className="absolute left-3.5 top-1/2 -translate-y-1/2 text-slate-400 pointer-events-none" />
                            </div>
                        </div>

                        {/* 4. Actions Box */}
                        <div className="bg-indigo-600 rounded-2xl p-5 shadow-lg shadow-indigo-200 flex flex-col gap-3 text-center">
                            <button
                                type="submit"
                                disabled={submitting}
                                className="w-full bg-white/20 hover:bg-white/30 text-white border border-white/20 rounded-xl py-3 font-bold flex items-center justify-center gap-2 transition-all active:scale-95"
                            >
                                {submitting ? <Loader2 size={20} className="animate-spin" /> : <Save size={20} />}
                                {isEditing ? 'Save Changes' : 'Create Task'}
                            </button>
                            <p className="text-indigo-200 text-xs">Remember to save your changes.</p>
                        </div>

                    </div>

                </div>
            </motion.form>
        </div>
    );
};

export default AddTaskPage;
