import React, { useState, useEffect } from "react";
import {
  Plus,
  Calendar,
  User as UserIcon,
  CheckCircle,
  Clock,
  AlertCircle,
  X,
  Filter,
  Layout,
  MoreHorizontal,
  Search,
  ChevronRight
} from "lucide-react";
import { taskService, Task } from "../../services/taskService";
import { userService } from "../../services/userService";
import { User as ApiUser } from "../../services/authService";

const TASKS_STATUSES = ["Todo", "In Progress", "Completed"] as const;

const TasksPage: React.FC = () => {
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [users, setUsers] = useState<ApiUser[]>([]);
  const [searchQuery, setSearchQuery] = useState("");

  // Drawer State
  const [isDrawerOpen, setIsDrawerOpen] = useState(false);
  const [editingTask, setEditingTask] = useState<Partial<Task> | null>(null);

  // Load Data
  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [fetchedTasks, fetchedUsers] = await Promise.all([
        taskService.getTasks(),
        userService.getAllUsers()
      ]);
      setTasks(fetchedTasks);
      setUsers(fetchedUsers);
    } catch (error) {
      console.error("Error loading data", error);
    } finally {
      setLoading(false);
    }
  };

  // derived
  const filteredTasks = tasks.filter(t =>
    t.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    t.description?.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const groupedTasks = {
    Todo: filteredTasks.filter(t => t.status === "Todo"),
    "In Progress": filteredTasks.filter(t => t.status === "In Progress"),
    Completed: filteredTasks.filter(t => t.status === "Completed"),
  };

  // Actions
  const handleSave = async (taskData: any) => {
    try {
      if (taskData.id) {
        // Update
        await taskService.updateTask(taskData.id, taskData);
        setTasks(prev => prev.map(t => t.id === taskData.id ? { ...t, ...taskData } : t));
      } else {
        // Create
        const newTask = await taskService.createTask(taskData);
        setTasks(prev => [...prev, newTask]);
      }
      setIsDrawerOpen(false);
      setEditingTask(null);
    } catch (error) {
      console.error("Failed to save task", error);
    }
  };

  const handleStatusUpdate = async (taskId: number, newStatus: string) => {
    // Optimistic
    setTasks(prev => prev.map(t => t.id === taskId ? { ...t, status: newStatus as any } : t));
    try {
      await taskService.updateTask(taskId, { status: newStatus });
    } catch (e) {
      console.error("Status update failed", e);
      fetchData(); // Revert
    }
  };

  const openNewTask = () => {
    setEditingTask({ status: "Todo", title: "", description: "", assigned_to: undefined, due_date: "" });
    setIsDrawerOpen(true);
  };

  const openEditTask = (task: Task) => {
    setEditingTask(task);
    setIsDrawerOpen(true);
  };

  return (
    <div className="flex flex-col h-screen bg-white overflow-hidden">
      {/* Top Navigation / Toolbar */}
      <div className="h-16 border-b border-gray-100 flex items-center justify-between px-6 bg-white shrink-0 z-20">
        <div className="flex items-center gap-4">
          <h1 className="text-xl font-bold text-slate-800">Tasks</h1>
          <div className="h-6 w-px bg-gray-200"></div>
          <div className="flex items-center gap-2 text-sm text-slate-500 bg-gray-50 px-3 py-1.5 rounded-lg">
            <Layout size={16} />
            <span>Board View</span>
          </div>
        </div>

        <div className="flex items-center gap-3">
          <div className="relative">
            <Search size={16} className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
            <input
              type="text"
              placeholder="Filter tasks..."
              value={searchQuery}
              onChange={e => setSearchQuery(e.target.value)}
              className="pl-9 pr-4 py-1.5 bg-gray-50 border border-transparent focus:bg-white focus:border-blue-200 focus:ring-2 focus:ring-blue-50 rounded-lg text-sm transition-all"
            />
          </div>
          <button className="p-2 text-slate-400 hover:text-slate-600 hover:bg-gray-50 rounded-lg transition">
            <Filter size={18} />
          </button>
          <button
            onClick={openNewTask}
            className="flex items-center gap-2 bg-slate-900 text-white px-4 py-2 rounded-lg text-sm font-medium hover:bg-slate-800 transition shadow-sm hover:shadow"
          >
            <Plus size={16} /> New Task
          </button>
        </div>
      </div>

      {/* Kanban Board Area */}
      <div className="flex-1 overflow-x-auto overflow-y-hidden bg-slate-50/50 p-6">
        <div className="flex gap-6 h-full min-w-[1000px]">
          {TASKS_STATUSES.map(status => (
            <div key={status} className="flex-1 flex flex-col h-full min-w-[320px] max-w-sm">
              {/* Column Header */}
              <div className="flex items-center justify-between mb-4 px-1">
                <div className="flex items-center gap-2">
                  <div className={`w-2 h-2 rounded-full ${status === 'Todo' ? 'bg-slate-400' : status === 'In Progress' ? 'bg-indigo-500' : 'bg-green-500'}`}></div>
                  <h3 className="font-semibold text-slate-700 text-sm tracking-wide uppercase">{status}</h3>
                  <span className="text-xs text-slate-400 font-medium ml-1">{groupedTasks[status].length}</span>
                </div>
                <button
                  onClick={openNewTask}
                  className="text-slate-400 hover:text-slate-600 p-1 hover:bg-gray-200 rounded transition"
                >
                  <Plus size={14} />
                </button>
              </div>

              {/* Drop Zone / List */}
              <div className="flex-1 overflow-y-auto pr-2 space-y-3 pb-10 scrollbar-thin scrollbar-thumb-gray-200">
                {groupedTasks[status].length === 0 ? (
                  <div className="h-24 border border-dashed border-gray-200 rounded-xl flex items-center justify-center">
                    <span className="text-xs text-gray-400 font-medium">No tasks</span>
                  </div>
                ) : (
                  groupedTasks[status].map(task => (
                    <div
                      key={task.id}
                      onClick={() => openEditTask(task)}
                      className="group bg-white p-4 rounded-xl border border-gray-100 shadow-sm hover:shadow-md hover:border-indigo-100 transition-all cursor-pointer relative"
                    >
                      <div className="flex justify-between items-start mb-2">
                        <h4 className="font-medium text-slate-800 text-sm leading-snug line-clamp-2">{task.title}</h4>
                        <button className="opacity-0 group-hover:opacity-100 text-gray-400 hover:text-gray-600 transition">
                          <MoreHorizontal size={16} />
                        </button>
                      </div>

                      <div className="flex items-center justify-between mt-3">
                        <div className="flex items-center -space-x-1.5">
                          {task.assigned_to_user ? (
                            <div className="w-6 h-6 rounded-full bg-indigo-100 border border-white text-indigo-600 flex items-center justify-center text-[10px] font-bold" title={task.assigned_to_user.full_name}>
                              {task.assigned_to_user.full_name.charAt(0)}
                            </div>
                          ) : (
                            <div className="w-6 h-6 rounded-full bg-gray-100 border border-white flex items-center justify-center text-gray-400">
                              <UserIcon size={12} />
                            </div>
                          )}
                        </div>

                        {task.due_date && (
                          <div className={`flex items-center gap-1 text-[10px] font-medium px-2 py-0.5 rounded-full ${new Date(task.due_date) < new Date() && status !== 'Completed'
                              ? 'bg-red-50 text-red-600'
                              : 'bg-gray-50 text-gray-500'
                            }`}>
                            <Clock size={10} />
                            {new Date(task.due_date).toLocaleDateString(undefined, { month: 'short', day: 'numeric' })}
                          </div>
                        )}
                      </div>

                      {/* Quick Move Utility (Hover) */}
                      {status !== 'Completed' && (
                        <button
                          onClick={(e) => {
                            e.stopPropagation();
                            handleStatusUpdate(task.id, status === 'Todo' ? 'In Progress' : 'Completed');
                          }}
                          className="absolute right-3 bottom-3 opacity-0 group-hover:opacity-100 p-1 bg-indigo-50 hover:bg-indigo-100 text-indigo-600 rounded-lg transition-all"
                          title="Move Next"
                        >
                          <ChevronRight size={14} />
                        </button>
                      )}
                    </div>
                  ))
                )}
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Right Drawer (Slide Over) */}
      <div
        className={`fixed inset-y-0 right-0 w-full md:w-[480px] bg-white shadow-2xl transform transition-transform duration-300 ease-in-out z-50 flex flex-col ${isDrawerOpen ? 'translate-x-0' : 'translate-x-full'}`}
      >
        {editingTask && (
          <>
            {/* Drawer Header */}
            <div className="px-6 py-5 border-b border-gray-100 flex items-center justify-between bg-gray-50/50">
              <div>
                <h2 className="text-lg font-bold text-slate-800">{editingTask.id ? 'Edit Task' : 'Create Task'}</h2>
                <div className="flex items-center gap-2 mt-1">
                  <span className="text-xs text-slate-500 uppercase tracking-wider font-semibold">Status:</span>
                  <select
                    value={editingTask.status}
                    onChange={e => setEditingTask({ ...editingTask, status: e.target.value as any })}
                    className="text-xs bg-white border border-gray-200 rounded px-2 py-0.5 outline-none focus:border-indigo-500 text-slate-700 font-medium"
                  >
                    {TASKS_STATUSES.map(s => <option key={s} value={s}>{s}</option>)}
                  </select>
                </div>
              </div>
              <button onClick={() => setIsDrawerOpen(false)} className="text-gray-400 hover:text-gray-700 hover:bg-gray-100 p-2 rounded-full transition">
                <X size={20} />
              </button>
            </div>

            {/* Drawer Body - Form */}
            <div className="flex-1 overflow-y-auto p-6 space-y-6">
              <div className="space-y-2">
                <label className="text-sm font-semibold text-slate-700">Task Title</label>
                <input
                  type="text"
                  value={editingTask.title}
                  onChange={e => setEditingTask({ ...editingTask, title: e.target.value })}
                  placeholder="e.g. Redesign Homepage"
                  className="w-full text-lg font-medium placeholder:font-normal px-4 py-3 bg-white border border-gray-200 rounded-xl focus:border-indigo-500 focus:ring-4 focus:ring-indigo-500/10 outline-none transition-all"
                />
              </div>

              <div className="space-y-2">
                <label className="text-sm font-semibold text-slate-700">Description</label>
                <textarea
                  rows={6}
                  value={editingTask.description}
                  onChange={e => setEditingTask({ ...editingTask, description: e.target.value })}
                  placeholder="Add detailed description..."
                  className="w-full px-4 py-3 bg-gray-50 border border-gray-200 rounded-xl focus:bg-white focus:border-indigo-500 focus:ring-4 focus:ring-indigo-500/10 outline-none transition-all resize-none text-sm text-slate-600 leading-relaxed"
                />
              </div>

              <div className="grid grid-cols-2 gap-5">
                <div>
                  <label className="block text-xs font-semibold text-gray-500 uppercase mb-2">Assignee</label>
                  <div className="relative">
                    <UserIcon size={16} className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
                    <select
                      value={editingTask.assigned_to || ""}
                      onChange={e => setEditingTask({ ...editingTask, assigned_to: e.target.value ? Number(e.target.value) : undefined })}
                      className="w-full pl-9 pr-3 py-2.5 bg-white border border-gray-200 rounded-lg text-sm focus:border-indigo-500 outline-none appearance-none"
                    >
                      <option value="">Unassigned</option>
                      {users.map(u => (
                        <option key={u.id} value={u.id}>{u.full_name}</option>
                      ))}
                    </select>
                  </div>
                </div>

                <div>
                  <label className="block text-xs font-semibold text-gray-500 uppercase mb-2">Due Date</label>
                  <div className="relative">
                    <Calendar size={16} className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
                    <input
                      type="date"
                      value={editingTask.due_date ? editingTask.due_date.split('T')[0] : ""}
                      onChange={e => setEditingTask({ ...editingTask, due_date: e.target.value })}
                      className="w-full pl-9 pr-3 py-2.5 bg-white border border-gray-200 rounded-lg text-sm focus:border-indigo-500 outline-none"
                    />
                  </div>
                </div>
              </div>
            </div>

            {/* Drawer Footer */}
            <div className="p-6 bg-gray-50 border-t border-gray-100 flex justify-end gap-3">
              <button
                onClick={() => setIsDrawerOpen(false)}
                className="px-5 py-2.5 text-gray-600 font-medium hover:bg-gray-200 rounded-xl transition-colors text-sm"
              >
                Cancel
              </button>
              <button
                onClick={() => handleSave(editingTask)}
                disabled={!editingTask.title}
                className="px-6 py-2.5 bg-slate-900 text-white font-semibold rounded-xl hover:bg-slate-800 disabled:opacity-50 disabled:cursor-not-allowed shadow-lg shadow-indigo-500/20 transition-all text-sm"
              >
                {editingTask.id ? 'Save Changes' : 'Create Task'}
              </button>
            </div>
          </>
        )}
      </div>

      {/* Dimmed Overlay for Drawer */}
      {isDrawerOpen && (
        <div
          className="fixed inset-0 bg-slate-900/20 backdrop-blur-[1px] z-40 transition-opacity"
          onClick={() => setIsDrawerOpen(false)}
        ></div>
      )}

    </div>
  );
};

export default TasksPage;
